// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../libraries/AequitasOrdering.sol";
import "../interfaces/IClearSettle.sol";

/**
 * @title TestAequitasHarness
 * @notice Test harness to expose AequitasOrdering library functions for testing
 * @dev This contract is ONLY for testing Tarjan's SCC algorithm and condensation graph
 */
contract TestAequitasHarness {

    /**
     * @notice Test wrapper for findStronglyConnectedComponents
     * @param txHashes Array of transaction hashes
     * @param edges Dependency edges
     * @return batches Atomic batches (SCCs)
     */
    function testFindSCCs(
        bytes32[] memory txHashes,
        DependencyEdge[] memory edges
    ) external pure returns (AtomicBatch[] memory batches) {
        return AequitasOrdering.findStronglyConnectedComponents(txHashes, edges);
    }

    /**
     * @notice Test wrapper for topological sort
     * @param batches Atomic batches to sort
     * @param edges Original dependency edges
     * @param txHashes Transaction hashes
     * @return sortedBatches Topologically sorted batches
     */
    function testTopologicalSort(
        AtomicBatch[] memory batches,
        DependencyEdge[] memory edges,
        bytes32[] memory txHashes
    ) external pure returns (AtomicBatch[] memory sortedBatches) {
        // First, extract sccIds from batches
        uint256[] memory sccIds = extractSCCIds(batches, txHashes);

        return AequitasOrdering.topologicalSort(batches, edges, sccIds, txHashes);
    }

    /**
     * @notice Test wrapper for buildDependencyGraph
     * @param txHashes Array of transaction hashes
     * @param validatorCount Number of validators
     * @return edges Dependency edges
     */
    function testBuildDependencyGraph(
        bytes32[] memory txHashes,
        uint256 validatorCount
    ) external view returns (DependencyEdge[] memory edges) {
        // This would normally use storage, but for testing we'll create a simple version
        // In real usage, receptionLogs would be in contract storage

        // For now, return empty array (full test would need storage setup)
        return new DependencyEdge[](0);
    }

    /**
     * @notice Extract SCC IDs from batches
     * @dev Helper function to map transactions to their SCC IDs
     * @param batches Atomic batches (SCCs)
     * @param txHashes All transaction hashes
     * @return sccIds Array where sccIds[i] = SCC id of txHashes[i]
     */
    function extractSCCIds(
        AtomicBatch[] memory batches,
        bytes32[] memory txHashes
    ) internal pure returns (uint256[] memory sccIds) {
        sccIds = new uint256[](txHashes.length);

        // Initialize all to max (unassigned)
        for (uint256 i = 0; i < txHashes.length; i++) {
            sccIds[i] = type(uint256).max;
        }

        // Assign SCC IDs based on batch membership
        for (uint256 sccId = 0; sccId < batches.length; sccId++) {
            bytes32[] memory txsInBatch = batches[sccId].transactions;

            for (uint256 j = 0; j < txsInBatch.length; j++) {
                bytes32 tx = txsInBatch[j];

                // Find this tx in txHashes array
                for (uint256 k = 0; k < txHashes.length; k++) {
                    if (txHashes[k] == tx) {
                        sccIds[k] = sccId;
                        break;
                    }
                }
            }
        }

        return sccIds;
    }

    /**
     * @notice Test wrapper for fairness threshold calculation
     * @param validatorCount Number of validators
     * @return threshold Fairness threshold
     */
    function testCalculateFairnessThreshold(
        uint256 validatorCount
    ) external pure returns (uint256 threshold) {
        return AequitasOrdering.calculateFairnessThreshold(validatorCount);
    }

    /**
     * @notice Test wrapper for edge fairness validation
     * @param edge Dependency edge to validate
     * @param validatorCount Total validators
     * @return valid True if edge meets fairness threshold
     */
    function testIsEdgeFair(
        DependencyEdge memory edge,
        uint256 validatorCount
    ) external pure returns (bool valid) {
        return AequitasOrdering.isEdgeFair(edge, validatorCount);
    }
}

contracts/oracles/ChainlinkOracleAdapter.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";

/// @title ChainlinkOracleAdapter
/// @notice Fetches price data from Chainlink decentralized oracle network
/// @dev Official Sepolia testnet addresses from https://docs.chain.link/data-feeds/price-feeds/addresses
contract ChainlinkOracleAdapter {

    struct PriceFeed {
        AggregatorV3Interface feed;
        uint8 decimals;
        string description;
        uint256 heartbeat; // Max acceptable staleness (seconds)
    }

    // Feed registry: asset pair => Chainlink aggregator
    mapping(bytes32 => PriceFeed) public priceFeeds;

    // Chainlink aggregator addresses (Sepolia testnet)
    // Source: https://docs.chain.link/data-feeds/price-feeds/addresses
    address public constant ETH_USD_FEED = 0x694AA1769357215DE4FAC081bf1f309aDC325306;
    address public constant BTC_USD_FEED = 0x1b44F3514812d835EB1BDB0acB33d3fA3351Ee43;
    address public constant LINK_USD_FEED = 0xc59E3633BAAC79493d908e63626716e204A45EdF;

    event PriceFeedRegistered(bytes32 indexed pairId, address feedAddress, string description);
    event PriceFetched(bytes32 indexed pairId, uint256 price, uint256 timestamp, uint80 roundId);

    constructor() {
        // Register ETH/USD feed
        _registerFeed(
            keccak256("ETH/USD"),
            ETH_USD_FEED,
            8,
            "ETH/USD Chainlink",
            3600 // 1 hour max staleness
        );

        // Register BTC/USD feed
        _registerFeed(
            keccak256("BTC/USD"),
            BTC_USD_FEED,
            8,
            "BTC/USD Chainlink",
            3600
        );

        // Register LINK/USD feed
        _registerFeed(
            keccak256("LINK/USD"),
            LINK_USD_FEED,
            8,
            "LINK/USD Chainlink",
            3600
        );
    }

    /// @notice Register a new price feed
    /// @param pairId Identifier for the trading pair
    /// @param feedAddress Chainlink aggregator address
    /// @param decimals Number of decimals in the feed
    /// @param description Human-readable description
    /// @param heartbeat Maximum acceptable staleness in seconds
    function _registerFeed(
        bytes32 pairId,
        address feedAddress,
        uint8 decimals,
        string memory description,
        uint256 heartbeat
    ) internal {
        priceFeeds[pairId] = PriceFeed({
            feed: AggregatorV3Interface(feedAddress),
            decimals: decimals,
            description: description,
            heartbeat: heartbeat
        });

        emit PriceFeedRegistered(pairId, feedAddress, description);
    }

    /// @notice Fetch latest price from Chainlink
    /// @param pairId Asset pair identifier (e.g., keccak256("ETH/USD"))
    /// @return price Latest price (normalized to 18 decimals)
    /// @return timestamp When price was last updated
    /// @return roundId Chainlink round ID for verification
    function getLatestPrice(bytes32 pairId)
        external
        view
        returns (
            uint256 price,
            uint256 timestamp,
            uint80 roundId
        )
    {
        PriceFeed memory feed = priceFeeds[pairId];
        require(address(feed.feed) != address(0), "ChainlinkAdapter: Feed not registered");

        (
            uint80 _roundId,
            int256 answer,
            ,
            uint256 updatedAt,
            uint80 answeredInRound
        ) = feed.feed.latestRoundData();

        // Validation checks
        require(answer > 0, "ChainlinkAdapter: Invalid price (non-positive)");
        require(updatedAt > 0, "ChainlinkAdapter: Invalid timestamp");
        require(answeredInRound >= _roundId, "ChainlinkAdapter: Stale price (round mismatch)");

        // Staleness check
        require(
            block.timestamp - updatedAt <= feed.heartbeat,
            "ChainlinkAdapter: Price too stale"
        );

        // Normalize to 18 decimals (Chainlink uses 8)
        uint256 normalizedPrice = uint256(answer) * 1e10;

        return (normalizedPrice, updatedAt, _roundId);
    }

    /// @notice Get price from specific historical round
    /// @dev Used for dispute resolution (verify historical claim)
    /// @param pairId Asset pair identifier
    /// @param roundId Historical round ID to query
    /// @return price Historical price (normalized to 18 decimals)
    /// @return timestamp When that round was finalized
    function getHistoricalPrice(bytes32 pairId, uint80 roundId)
        external
        view
        returns (
            uint256 price,
            uint256 timestamp
        )
    {
        PriceFeed memory feed = priceFeeds[pairId];
        require(address(feed.feed) != address(0), "ChainlinkAdapter: Feed not registered");

        (
            ,
            int256 answer,
            ,
            uint256 updatedAt,
            uint80 answeredInRound
        ) = feed.feed.getRoundData(roundId);

        require(answer > 0, "ChainlinkAdapter: Invalid historical price");
        require(answeredInRound == roundId, "ChainlinkAdapter: Round not finalized");

        uint256 normalizedPrice = uint256(answer) * 1e10;
        return (normalizedPrice, updatedAt);
    }

    /// @notice Check if a feed is registered
    /// @param pairId Asset pair to check
    /// @return True if feed exists
    function isFeedRegistered(bytes32 pairId) external view returns (bool) {
        return address(priceFeeds[pairId].feed) != address(0);
    }

    /// @notice Get feed information
    /// @param pairId Asset pair to query
    /// @return feedAddress Chainlink aggregator address
    /// @return decimals Feed decimals
    /// @return description Feed description
    /// @return heartbeat Max staleness in seconds
    function getFeedInfo(bytes32 pairId)
        external
        view
        returns (
            address feedAddress,
            uint8 decimals,
            string memory description,
            uint256 heartbeat
        )
    {
        PriceFeed memory feed = priceFeeds[pairId];
        return (
            address(feed.feed),
            feed.decimals,
            feed.description,
            feed.heartbeat
        );
    }
}

