// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";

/// @title PythOracleAdapter
/// @notice Fetches price data from Pyth Network (high-frequency oracle)
/// @dev Official Sepolia testnet address from https://docs.pyth.network/price-feeds/contract-addresses/evm
contract PythOracleAdapter {

    IPyth public pyth;

    // Pyth contract address (Ethereum Sepolia testnet)
    // Source: https://docs.pyth.network/price-feeds/contract-addresses/evm
    address public constant PYTH_ADDRESS = 0xDd24F84d36BF92C65F92307595335bdFab5Bbd21;

    // Price feed IDs (Pyth identifiers)
    // Source: https://pyth.network/developers/price-feed-ids
    bytes32 public constant ETH_USD_FEED_ID =
        0xff61491a931112ddf1bd8147cd1b641375f79f5825126d665480874634fd0ace;
    bytes32 public constant BTC_USD_FEED_ID =
        0xe62df6c8b4a85fe1a67db44dc12de5db330f7ac66b72dc658afedf0f4a415b43;

    mapping(bytes32 => bytes32) public feedIds; // pairId => Pyth feed ID

    event PriceFeedRegistered(bytes32 indexed pairId, bytes32 indexed feedId);
    event PriceFetched(bytes32 indexed pairId, uint256 price, uint256 timestamp, uint64 conf);

    constructor() {
        pyth = IPyth(PYTH_ADDRESS);

        // Register ETH/USD
        feedIds[keccak256("ETH/USD")] = ETH_USD_FEED_ID;
        emit PriceFeedRegistered(keccak256("ETH/USD"), ETH_USD_FEED_ID);

        // Register BTC/USD
        feedIds[keccak256("BTC/USD")] = BTC_USD_FEED_ID;
        emit PriceFeedRegistered(keccak256("BTC/USD"), BTC_USD_FEED_ID);
    }

    /// @notice Get latest price from Pyth (normalized to 18 decimals)
    /// @param pairId Asset pair identifier (e.g., keccak256("ETH/USD"))
    /// @return price Normalized price (18 decimals)
    /// @return timestamp Publish time
    /// @return conf Confidence interval
    function getLatestPrice(bytes32 pairId)
        external
        view
        returns (
            uint256 price,
            uint256 timestamp,
            uint64 conf
        )
    {
        bytes32 feedId = feedIds[pairId];
        require(feedId != bytes32(0), "PythAdapter: Feed not registered");

        PythStructs.Price memory priceData = pyth.getPriceUnsafe(feedId);

        // Validation
        require(priceData.price > 0, "PythAdapter: Invalid price");
        require(priceData.publishTime > 0, "PythAdapter: Invalid timestamp");

        // Staleness check (Pyth updates every 400ms, allow 60s max)
        require(
            block.timestamp - priceData.publishTime <= 60,
            "PythAdapter: Pyth price too stale"
        );

        // Normalize to 18 decimals
        uint256 normalizedPrice = _normalizePrice(
            uint64(priceData.price),
            priceData.expo
        );

        return (normalizedPrice, priceData.publishTime, priceData.conf);
    }

    /// @notice Normalize Pyth price to 18 decimals
    /// @dev Pyth prices come with exponent (e.g., price=3000, expo=-8 means $30.00)
    /// @param price Raw price value
    /// @param expo Exponent (negative for decimal places)
    /// @return Normalized price with 18 decimals
    function _normalizePrice(uint64 price, int32 expo) internal pure returns (uint256) {
        // Pyth prices: price * 10^expo
        // Target: 18 decimals

        if (expo < 0) {
            uint32 absExpo = uint32(-expo);
            if (absExpo < 18) {
                // Scale up to 18 decimals
                return uint256(price) * (10 ** (18 - absExpo));
            } else {
                // Already more than 18 decimals, scale down
                return uint256(price) / (10 ** (absExpo - 18));
            }
        } else {
            // Positive exponent (rare)
            return uint256(price) * (10 ** (18 + uint32(expo)));
        }
    }

    /// @notice Update Pyth price with off-chain data
    /// @dev Pyth requires price update data from Hermes API
    /// @param priceUpdateData Price update data from Pyth Hermes
    function updatePrice(bytes[] calldata priceUpdateData)
        external
        payable
    {
        uint256 fee = pyth.getUpdateFee(priceUpdateData);
        require(msg.value >= fee, "PythAdapter: Insufficient update fee");

        pyth.updatePriceFeeds{value: fee}(priceUpdateData);
    }

    /// @notice Get Pyth price without safety checks (unsafe - use with caution)
    /// @dev Only use this if you need raw data and handle validation yourself
    /// @param pairId Asset pair identifier
    /// @return priceData Raw Pyth price struct
    function getPriceUnsafe(bytes32 pairId)
        external
        view
        returns (PythStructs.Price memory priceData)
    {
        bytes32 feedId = feedIds[pairId];
        require(feedId != bytes32(0), "PythAdapter: Feed not registered");

        return pyth.getPriceUnsafe(feedId);
    }

    /// @notice Get the current update fee for Pyth
    /// @param updateDataSize Number of price updates
    /// @return fee Required fee in wei
    function getUpdateFee(uint256 updateDataSize) external view returns (uint256) {
        bytes[] memory dummyData = new bytes[](updateDataSize);
        return pyth.getUpdateFee(dummyData);
    }

    /// @notice Check if feed is registered
    /// @param pairId Asset pair to check
    /// @return True if feed registered
    function isFeedRegistered(bytes32 pairId) external view returns (bool) {
        return feedIds[pairId] != bytes32(0);
    }
}

contracts/oracles/UniswapV3TWAPAdapter.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";

/// @title UniswapV3TWAPAdapter
/// @notice Fetches time-weighted average price from Uniswap V3 pools
/// @dev Provides on-chain price oracle resistant to flash loan manipulation
/// @dev Pool addresses from https://www.geckoterminal.com/sepolia-testnet
contract UniswapV3TWAPAdapter {

    struct PoolConfig {
        address pool;
        address baseToken;  // Token being priced (e.g., WETH)
        address quoteToken; // Token price is quoted in (e.g., USDC)
        uint8 baseDecimals;
        uint8 quoteDecimals;
        uint32 twapPeriod;  // Time window for TWAP (seconds)
        bool isToken0Base;  // True if baseToken is token0 in the pool
    }

    mapping(bytes32 => PoolConfig) public pools;

    // Uniswap V3 pool addresses (Sepolia testnet)
    // Source: https://www.geckoterminal.com/sepolia-testnet/pools/0x9799b5edc1aa7d3fad350309b08df3f64914e244
    address public constant USDC_WETH_POOL_03 = 0x9799b5EDC1aA7D3FAd350309B08df3F64914E244; // 0.3% fee

    // Token addresses on Sepolia (need to verify these)
    address public constant WETH = 0xfFf9976782d46CC05630D1f6eBAb18b2324d6B14; // WETH on Sepolia
    address public constant USDC = 0x1c7D4B196Cb0C7B01d743Fbc6116a902379C7238; // USDC on Sepolia

    event PoolRegistered(bytes32 indexed pairId, address poolAddress, uint32 twapPeriod);
    event TWAPCalculated(bytes32 indexed pairId, uint256 price, uint256 timestamp);

    constructor() {
        // Register WETH/USDC pool
        // Note: This gives price of WETH in USDC, we map it to ETH/USD
        pools[keccak256("ETH/USD")] = PoolConfig({
            pool: USDC_WETH_POOL_03,
            baseToken: WETH,
            quoteToken: USDC,
            baseDecimals: 18,  // WETH has 18 decimals
            quoteDecimals: 6,  // USDC has 6 decimals
            twapPeriod: 1800,  // 30 minute TWAP (balance between freshness and security)
            isToken0Base: false // WETH is typically token1, USDC is token0 (lower address)
        });

        emit PoolRegistered(keccak256("ETH/USD"), USDC_WETH_POOL_03, 1800);
    }

    /// @notice Get time-weighted average price from Uniswap V3
    /// @param pairId Asset pair identifier (e.g., keccak256("ETH/USD"))
    /// @return price TWAP price (normalized to 18 decimals)
    /// @return timestamp Current block timestamp (TWAP is current)
    function getLatestPrice(bytes32 pairId)
        external
        view
        returns (
            uint256 price,
            uint256 timestamp
        )
    {
        PoolConfig memory config = pools[pairId];
        require(config.pool != address(0), "UniswapAdapter: Pool not configured");

        IUniswapV3Pool pool = IUniswapV3Pool(config.pool);

        // Get TWAP tick (arithmetic mean over the period)
        int24 arithmeticMeanTick = _getTWAPTick(pool, config.twapPeriod);

        // Convert tick to price
        uint256 quoteAmount = _getQuoteAtTick(
            arithmeticMeanTick,
            uint128(10 ** config.baseDecimals), // 1 unit of base token (e.g., 1 WETH)
            config.isToken0Base
        );

        // Normalize to 18 decimals
        uint256 normalizedPrice;
        if (config.quoteDecimals < 18) {
            // Scale up (e.g., USDC has 6 decimals, multiply by 10^12)
            normalizedPrice = quoteAmount * (10 ** (18 - config.quoteDecimals));
        } else {
            // Scale down (rare case)
            normalizedPrice = quoteAmount / (10 ** (config.quoteDecimals - 18));
        }

        return (normalizedPrice, block.timestamp);
    }

    /// @notice Calculate TWAP tick from Uniswap V3 pool observations
    /// @dev Queries pool's oracle observations and calculates arithmetic mean tick
    /// @param pool Uniswap V3 pool contract
    /// @param period TWAP period in seconds
    /// @return arithmeticMeanTick The time-weighted average tick
    function _getTWAPTick(IUniswapV3Pool pool, uint32 period)
        internal
        view
        returns (int24 arithmeticMeanTick)
    {
        uint32[] memory secondsAgos = new uint32[](2);
        secondsAgos[0] = period; // Start of TWAP window
        secondsAgos[1] = 0;      // Current time

        // Get cumulative tick data from pool
        (int56[] memory tickCumulatives, ) = pool.observe(secondsAgos);

        // Calculate arithmetic mean tick over the period
        int56 tickCumulativesDelta = tickCumulatives[1] - tickCumulatives[0];
        arithmeticMeanTick = int24(tickCumulativesDelta / int56(uint56(period)));

        // Round down for negative ticks
        if (tickCumulativesDelta < 0 && (tickCumulativesDelta % int56(uint56(period)) != 0)) {
            arithmeticMeanTick--;
        }
    }

    /// @notice Convert Uniswap V3 tick to quote amount
    /// @dev Simplified version of OracleLibrary.getQuoteAtTick for Solidity 0.8
    /// @param tick The tick to convert
    /// @param baseAmount Amount of base token (with decimals)
    /// @param isToken0Base True if base token is token0
    /// @return quoteAmount The corresponding amount of quote token
    function _getQuoteAtTick(
        int24 tick,
        uint128 baseAmount,
        bool isToken0Base
    ) internal pure returns (uint256 quoteAmount) {
        // Get sqrtPrice from tick
        uint160 sqrtPriceX96 = _getSqrtRatioAtTick(tick);

        // Calculate price from sqrtPriceX96
        // sqrtPriceX96 = sqrt(token1/token0) * 2^96
        // price = (sqrtPriceX96 / 2^96)^2

        if (sqrtPriceX96 <= type(uint128).max) {
            uint256 priceX192 = uint256(sqrtPriceX96) * sqrtPriceX96;
            quoteAmount = isToken0Base
                ? _mulDiv(priceX192, baseAmount, 1 << 192)
                : _mulDiv(1 << 192, baseAmount, priceX192);
        } else {
            uint256 priceX128 = _mulDiv(sqrtPriceX96, sqrtPriceX96, 1 << 64);
            quoteAmount = isToken0Base
                ? _mulDiv(priceX128, baseAmount, 1 << 128)
                : _mulDiv(1 << 128, baseAmount, priceX128);
        }
    }

    /// @notice Get sqrtPriceX96 from tick
    /// @dev Simplified implementation for common tick ranges
    /// @param tick The tick to convert
    /// @return sqrtPriceX96 The sqrt price encoded as a Q64.96
    function _getSqrtRatioAtTick(int24 tick) internal pure returns (uint160 sqrtPriceX96) {
        uint256 absTick = tick < 0 ? uint256(-int256(tick)) : uint256(int256(tick));
        require(absTick <= uint256(int256(type(int24).max)), "T");

        uint256 ratio = absTick & 0x1 != 0
            ? 0xfffcb933bd6fad37aa2d162d1a594001
            : 0x100000000000000000000000000000000;
        if (absTick & 0x2 != 0) ratio = (ratio * 0xfff97272373d413259a46990580e213a) >> 128;
        if (absTick & 0x4 != 0) ratio = (ratio * 0xfff2e50f5f656932ef12357cf3c7fdcc) >> 128;
        if (absTick & 0x8 != 0) ratio = (ratio * 0xffe5caca7e10e4e61c3624eaa0941cd0) >> 128;
        if (absTick & 0x10 != 0) ratio = (ratio * 0xffcb9843d60f6159c9db58835c926644) >> 128;
        if (absTick & 0x20 != 0) ratio = (ratio * 0xff973b41fa98c081472e6896dfb254c0) >> 128;
        if (absTick & 0x40 != 0) ratio = (ratio * 0xff2ea16466c96a3843ec78b326b52861) >> 128;
        if (absTick & 0x80 != 0) ratio = (ratio * 0xfe5dee046a99a2a811c461f1969c3053) >> 128;
        if (absTick & 0x100 != 0) ratio = (ratio * 0xfcbe86c7900a88aedcffc83b479aa3a4) >> 128;
        if (absTick & 0x200 != 0) ratio = (ratio * 0xf987a7253ac413176f2b074cf7815e54) >> 128;
        if (absTick & 0x400 != 0) ratio = (ratio * 0xf3392b0822b70005940c7a398e4b70f3) >> 128;
        if (absTick & 0x800 != 0) ratio = (ratio * 0xe7159475a2c29b7443b29c7fa6e889d9) >> 128;
        if (absTick & 0x1000 != 0) ratio = (ratio * 0xd097f3bdfd2022b8845ad8f792aa5825) >> 128;
        if (absTick & 0x2000 != 0) ratio = (ratio * 0xa9f746462d870fdf8a65dc1f90e061e5) >> 128;
        if (absTick & 0x4000 != 0) ratio = (ratio * 0x70d869a156d2a1b890bb3df62baf32f7) >> 128;
        if (absTick & 0x8000 != 0) ratio = (ratio * 0x31be135f97d08fd981231505542fcfa6) >> 128;
        if (absTick & 0x10000 != 0) ratio = (ratio * 0x9aa508b5b7a84e1c677de54f3e99bc9) >> 128;
        if (absTick & 0x20000 != 0) ratio = (ratio * 0x5d6af8dedb81196699c329225ee604) >> 128;
        if (absTick & 0x40000 != 0) ratio = (ratio * 0x2216e584f5fa1ea926041bedfe98) >> 128;
        if (absTick & 0x80000 != 0) ratio = (ratio * 0x48a170391f7dc42444e8fa2) >> 128;

        if (tick > 0) ratio = type(uint256).max / ratio;

        sqrtPriceX96 = uint160((ratio >> 32) + (ratio % (1 << 32) == 0 ? 0 : 1));
    }

    /// @notice Full precision multiplication
    /// @dev Calculates floor(a×b÷denominator) with full precision
    /// @param a The multiplicand
    /// @param b The multiplier
    /// @param denominator The divisor
    /// @return result The 256-bit result
    function _mulDiv(
        uint256 a,
        uint256 b,
        uint256 denominator
    ) internal pure returns (uint256 result) {
        uint256 prod0;
        uint256 prod1;
        assembly {
            let mm := mulmod(a, b, not(0))
            prod0 := mul(a, b)
            prod1 := sub(sub(mm, prod0), lt(mm, prod0))
        }

        if (prod1 == 0) {
            require(denominator > 0);
            assembly {
                result := div(prod0, denominator)
            }
            return result;
        }

        require(denominator > prod1);

        uint256 remainder;
        assembly {
            remainder := mulmod(a, b, denominator)
        }
        assembly {
            prod1 := sub(prod1, gt(remainder, prod0))
            prod0 := sub(prod0, remainder)
        }

        uint256 twos = denominator & (~denominator + 1);
        assembly {
            denominator := div(denominator, twos)
        }

        assembly {
            prod0 := div(prod0, twos)
        }
        assembly {
            twos := add(div(sub(0, twos), twos), 1)
        }
        prod0 |= prod1 * twos;

        uint256 inv = (3 * denominator) ^ 2;
        inv *= 2 - denominator * inv;
        inv *= 2 - denominator * inv;
        inv *= 2 - denominator * inv;
        inv *= 2 - denominator * inv;
        inv *= 2 - denominator * inv;
        inv *= 2 - denominator * inv;

        result = prod0 * inv;
        return result;
    }

    /// @notice Check if pool has sufficient history for TWAP
    /// @dev Verifies pool has observations covering the TWAP period
    /// @param pairId Asset pair to check
    /// @return True if pool is healthy and has sufficient data
    function isPoolHealthy(bytes32 pairId) external view returns (bool) {
        PoolConfig memory config = pools[pairId];
        if (config.pool == address(0)) return false;

        IUniswapV3Pool pool = IUniswapV3Pool(config.pool);

        uint32[] memory secondsAgos = new uint32[](2);
        secondsAgos[0] = config.twapPeriod;
        secondsAgos[1] = 0;

        // Check if pool has observations for TWAP period
        try pool.observe(secondsAgos) returns (
            int56[] memory,
            uint160[] memory
        ) {
            return true;
        } catch {
            return false;
        }
    }

    /// @notice Get pool configuration
    /// @param pairId Asset pair to query
    /// @return config Pool configuration struct
    function getPoolConfig(bytes32 pairId) external view returns (PoolConfig memory) {
        return pools[pairId];
    }

    /// @notice Check if pool is registered
    /// @param pairId Asset pair to check
    /// @return True if pool registered
    function isPoolRegistered(bytes32 pairId) external view returns (bool) {
        return pools[pairId].pool != address(0);
    }
}

contracts/SafetyGadget.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title SafetyGadget
 * @notice Module 5: Attack Model & Reorg Safety Implementation
 * @dev Provides protection against:
 *      1. Time-Bandit attacks via lookback distance enforcement
 *      2. Chain reorganizations via blockhash ancestry verification
 *      3. Double-settlement/replay attacks via nullifier pattern
 * 
 * Based on research specifications from Module 5:
 * - Lookback distance of 64 blocks (~12-15 mins on Ethereum)
 * - Nullifier = keccak256(sender || nonce || payloadHash || chainId)
 * - Ancestry verification using blockhash checks
 */
abstract contract SafetyGadget {
    // ============================================================
    //                        CONSTANTS
    // ============================================================

    /**
     * @notice Lookback distance in blocks before settlement can be finalized
     * @dev 64 blocks provides probabilistic finality on Ethereum PoS
     *      This prevents Time-Bandit attacks where miners rewrite recent history
     */
    uint256 public constant LOOKBACK_DISTANCE = 64;

    /**
     * @notice Maximum block age for which blockhash() returns non-zero
     * @dev EVM limitation - blockhash only works for last 256 blocks
     */
    uint256 public constant MAX_BLOCKHASH_AGE = 256;

    // ============================================================
    //                     FINALITY STATUS
    // ============================================================

    /**
     * @notice Finality status enum as per Module 5 spec
     * @dev PENDING: Transaction submitted but not yet logged
     *      LOGGED: Transaction included in a block
     *      CHECKPOINTED: Transaction is beyond lookback distance (safe from reorg)
     */
    enum FinalityStatus {
        PENDING,
        LOGGED,
        CHECKPOINTED
    }

    // ============================================================
    //                         STATE
    // ============================================================

    /**
     * @notice Mapping of nullifiers to track consumed settlement actions
     * @dev Nullifier = keccak256(sender || nonce || payloadHash || chainId)
     *      Once consumed, the same action cannot be replayed
     */
    mapping(bytes32 => bool) public nullifiers;

    /**
     * @notice Snapshot of chain state at commitment time
     * @param blockNumber The block number when snapshot was taken
     * @param blockHash The hash of the parent block at snapshot time
     * @param status Current finality status
     * @param exists Whether this snapshot has been initialized
     */
    struct ChainSnapshot {
        uint256 blockNumber;
        bytes32 blockHash;
        FinalityStatus status;
        bool exists;
    }

    /**
     * @notice Maps batch/settlement ID to its chain snapshot
     * @dev Used to verify ancestry and enforce lookback distance
     */
    mapping(bytes32 => ChainSnapshot) public chainSnapshots;

    // ============================================================
    //                         EVENTS
    // ============================================================

    /**
     * @notice Emitted when a nullifier is consumed
     * @param nullifier The consumed nullifier hash
     * @param sender The address that consumed the nullifier
     */
    event NullifierConsumed(bytes32 indexed nullifier, address indexed sender);

    /**
     * @notice Emitted when a chain snapshot is recorded
     * @param settlementId The settlement/batch ID
     * @param blockNumber The block number at snapshot time
     * @param blockHash The recorded block hash
     */
    event SnapshotRecorded(
        bytes32 indexed settlementId,
        uint256 blockNumber,
        bytes32 blockHash
    );

    /**
     * @notice Emitted when finality status changes
     * @param settlementId The settlement/batch ID
     * @param oldStatus Previous finality status
     * @param newStatus New finality status
     */
    event FinalityStatusChanged(
        bytes32 indexed settlementId,
        FinalityStatus oldStatus,
        FinalityStatus newStatus
    );

    /**
     * @notice Emitted when ancestry verification succeeds
     * @param settlementId The settlement/batch ID
     * @param verifiedAtBlock Block number where verification occurred
     */
    event AncestryVerified(bytes32 indexed settlementId, uint256 verifiedAtBlock);

    // ============================================================
    //                         ERRORS
    // ============================================================

    /// @notice Thrown when attempting to reuse a nullifier
    error NullifierAlreadyConsumed(bytes32 nullifier);

    /// @notice Thrown when lookback distance has not been met
    error LookbackDistanceNotMet(uint256 required, uint256 actual);

    /// @notice Thrown when a chain reorganization is detected
    error ReorgDetected(bytes32 expected, bytes32 actual);

    /// @notice Thrown when snapshot doesn't exist for given ID
    error SnapshotNotFound(bytes32 settlementId);

    /// @notice Thrown when blockhash is too old to verify
    error BlockhashTooOld(uint256 snapshotBlock, uint256 currentBlock);

    /// @notice Thrown when snapshot already exists
    error SnapshotAlreadyExists(bytes32 settlementId);

    // ============================================================
    //                    NULLIFIER FUNCTIONS
    // ============================================================

    /**
     * @notice Computes a nullifier for idempotence verification
     * @dev Nullifier = keccak256(sender || nonce || payloadHash || chainId)
     *      This binds the action to sender, sequence, content, AND chain
     * @param sender The address initiating the action
     * @param nonce The sender's nonce for this action
     * @param payloadHash Hash of the settlement payload data
     * @return The computed nullifier
     */
    function computeNullifier(
        address sender,
        uint256 nonce,
        bytes32 payloadHash
    ) public view returns (bytes32) {
        return keccak256(abi.encodePacked(sender, nonce, payloadHash, block.chainid));
    }

    /**
     * @notice Checks if a nullifier has been consumed
     * @param nullifier The nullifier to check
     * @return True if already consumed, false otherwise
     */
    function isNullifierConsumed(bytes32 nullifier) public view returns (bool) {
        return nullifiers[nullifier];
    }

    /**
     * @notice Internal function to consume a nullifier
     * @dev Reverts if nullifier already consumed
     * @param sender The address initiating the action
     * @param nonce The sender's nonce
     * @param payloadHash Hash of the payload
     * @return nullifier The consumed nullifier
     */
    function _consumeNullifier(
        address sender,
        uint256 nonce,
        bytes32 payloadHash
    ) internal returns (bytes32 nullifier) {
        nullifier = computeNullifier(sender, nonce, payloadHash);
        
        if (nullifiers[nullifier]) {
            revert NullifierAlreadyConsumed(nullifier);
        }
        
        nullifiers[nullifier] = true;
        emit NullifierConsumed(nullifier, sender);
    }

    // ============================================================
    //                   SNAPSHOT FUNCTIONS
    // ============================================================

    /**
     * @notice Records a chain snapshot for a settlement
     * @dev Called when settlement enters PRE_COMMITTED state
     *      Records current block number and parent block hash
     * @param settlementId Unique identifier for the settlement
     */
    function _recordSnapshot(bytes32 settlementId) internal {
        if (chainSnapshots[settlementId].exists) {
            revert SnapshotAlreadyExists(settlementId);
        }

        // Record the parent block hash (current block hash is 0x0 during execution)
        bytes32 parentHash = blockhash(block.number - 1);
        
        chainSnapshots[settlementId] = ChainSnapshot({
            blockNumber: block.number,
            blockHash: parentHash,
            status: FinalityStatus.LOGGED,
            exists: true
        });

        emit SnapshotRecorded(settlementId, block.number, parentHash);
        emit FinalityStatusChanged(settlementId, FinalityStatus.PENDING, FinalityStatus.LOGGED);
    }

    /**
     * @notice Gets the chain snapshot for a settlement
     * @param settlementId The settlement ID to query
     * @return The chain snapshot struct
     */
    function getSnapshot(bytes32 settlementId) public view returns (ChainSnapshot memory) {
        return chainSnapshots[settlementId];
    }

    /**
     * @notice Gets the current finality status of a settlement
     * @param settlementId The settlement ID to query
     * @return The current finality status
     */
    function getFinalityStatus(bytes32 settlementId) public view returns (FinalityStatus) {
        ChainSnapshot memory snapshot = chainSnapshots[settlementId];
        if (!snapshot.exists) {
            return FinalityStatus.PENDING;
        }
        
        // Check if we've passed the lookback distance
        if (block.number >= snapshot.blockNumber + LOOKBACK_DISTANCE) {
            return FinalityStatus.CHECKPOINTED;
        }
        
        return FinalityStatus.LOGGED;
    }

    // ============================================================
    //                 ANCESTRY VERIFICATION
    // ============================================================

    /**
     * @notice Verifies that the chain has not reorganized since snapshot
     * @dev Performs two critical checks:
     *      1. Lookback distance: Enough blocks have passed
     *      2. Ancestry: The recorded blockhash is still in the canonical chain
     * @param settlementId The settlement ID to verify
     */
    function _verifyAncestry(bytes32 settlementId) internal {
        ChainSnapshot storage snapshot = chainSnapshots[settlementId];
        
        if (!snapshot.exists) {
            revert SnapshotNotFound(settlementId);
        }

        // Check 1: Lookback Distance
        uint256 blocksPassed = block.number - snapshot.blockNumber;
        if (blocksPassed < LOOKBACK_DISTANCE) {
            revert LookbackDistanceNotMet(LOOKBACK_DISTANCE, blocksPassed);
        }

        // Check 2: Ancestry Verification (Reorg Detection)
        // Note: blockhash() only works for the last 256 blocks
        uint256 snapshotParentBlock = snapshot.blockNumber - 1;
        
        if (block.number - snapshotParentBlock > MAX_BLOCKHASH_AGE) {
            // If too old, we can't verify via blockhash
            // In production, this would require an external oracle or archive node
            // For hackathon scope, we assume settlements complete within 256 blocks
            revert BlockhashTooOld(snapshotParentBlock, block.number);
        }

        bytes32 currentHashOfSnapshotParent = blockhash(snapshotParentBlock);
        
        if (currentHashOfSnapshotParent != snapshot.blockHash) {
            revert ReorgDetected(snapshot.blockHash, currentHashOfSnapshotParent);
        }

        // Update status to CHECKPOINTED
        FinalityStatus oldStatus = snapshot.status;
        snapshot.status = FinalityStatus.CHECKPOINTED;
        
        emit AncestryVerified(settlementId, block.number);
        emit FinalityStatusChanged(settlementId, oldStatus, FinalityStatus.CHECKPOINTED);
    }

    /**
     * @notice View function to check if ancestry can be verified
     * @dev Does not modify state, useful for off-chain checks
     * @param settlementId The settlement ID to check
     * @return canVerify Whether ancestry verification would succeed
     * @return reason Description of why verification would fail (empty if success)
     */
    function canVerifyAncestry(bytes32 settlementId) 
        public 
        view 
        returns (bool canVerify, string memory reason) 
    {
        ChainSnapshot memory snapshot = chainSnapshots[settlementId];
        
        if (!snapshot.exists) {
            return (false, "Snapshot not found");
        }

        uint256 blocksPassed = block.number - snapshot.blockNumber;
        if (blocksPassed < LOOKBACK_DISTANCE) {
            return (false, "Lookback distance not met");
        }

        uint256 snapshotParentBlock = snapshot.blockNumber - 1;
        if (block.number - snapshotParentBlock > MAX_BLOCKHASH_AGE) {
            return (false, "Blockhash too old to verify");
        }

        bytes32 currentHashOfSnapshotParent = blockhash(snapshotParentBlock);
        if (currentHashOfSnapshotParent != snapshot.blockHash) {
            return (false, "Reorg detected - ancestry mismatch");
        }

        return (true, "");
    }

    // ============================================================
    //                    UTILITY FUNCTIONS
    // ============================================================

    /**
     * @notice Returns the number of blocks until lookback distance is met
     * @param settlementId The settlement ID to check
     * @return blocks Number of blocks remaining (0 if already met)
     */
    function blocksUntilCheckpoint(bytes32 settlementId) public view returns (uint256 blocks) {
        ChainSnapshot memory snapshot = chainSnapshots[settlementId];
        
        if (!snapshot.exists) {
            return LOOKBACK_DISTANCE;
        }

        uint256 targetBlock = snapshot.blockNumber + LOOKBACK_DISTANCE;
        if (block.number >= targetBlock) {
            return 0;
        }
        
        return targetBlock - block.number;
    }

    /**
     * @notice Checks if a settlement is safe to finalize
     * @param settlementId The settlement ID to check
     * @return True if lookback distance met and ancestry verified
     */
    function isSafeToFinalize(bytes32 settlementId) public view returns (bool) {
        (bool canVerify, ) = canVerifyAncestry(settlementId);
        return canVerify;
    }
}

